import { connect } from "react-redux";
import { useEffect } from "react";
import Product from "./product";
import { fetchMedicineProductsRequest,
  fetchMedicineProductsSuccess,
  fetchMedicineProductsFailure, } from "../redux/actions/productAction";

function Featured(props) {
  const { medicineProducts, isLoading, error, fetchMedicineProducts } = props;

  useEffect(() => {
    fetchMedicineProducts();
  }, [fetchMedicineProducts]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  return (
    <div className="featured-container">
      {medicineProducts.map((medicineProduct) => (
        <Product
          key={medicineProduct.id}
          subtitle="Essential for Women/Men"
          title={medicineProduct.name}
          desc={medicineProduct.description}
        />
      ))}
    </div>
  );
}

const mapStateToProps = (state) => ({
  medicineProducts: state.medicineProducts,
  isLoading: state.isLoading,
  error: state.error,
});

const mapDispatchToProps = {
  fetchMedicineProducts,
};

export default connect(mapStateToProps, mapDispatchToProps)(Featured);
