import React, { useEffect, useState } from "react";
import Product from "./product"; // Import the Product component
import "../styles/Featured.css";

function Featured() {
  const [medicineProducts, setMedicineProducts] = useState([]);

  useEffect(() => {
    async function fetchMedicineProduct() {
      try {
        const response = await fetch('http://localhost:3600/api/products/3');

        if (response.status === 200) {
          const medicineProduct = await response.json();
          setMedicineProducts([medicineProduct]);
        } else {
          console.error('Failed to fetch medicine product data');
        }
      } catch (error) {
        console.error('Error while fetching data:', error);
      }
    }

    fetchMedicineProduct();
  }, []);

  return (
    <div className="featured-container">
      <div className="container-header">
        <h2>Featured Products</h2>
        <div className="content-link">
          <a className="link-underline" href="#home">
            View All
          </a>
        </div>
      </div>
      <div className="featured-content">
        <div className="products">
          {medicineProducts.map((medicineProduct) => (
            <div key={medicineProduct.id} className="product-marker">
              {/* Assuming you have a Product component for displaying individual products */}
              <Product
                interactions={medicineProduct.interactions}
                title={medicineProduct.name}
                desc={medicineProduct.description}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Featured;
