import React from "react";
import "../styles/Featured.css";

const fetchMedicineProduct = async () => {
  const response = await fetch('http://localhost:3600/api/products/3');

  if (response.status === 200) {
    const medicineProductData = await response.json();

    return medicineProductData;
  }
};

const Products = ({ medicineProducts }) => {
  return (
    <div className="products">
      {medicineProducts.map((medicineProduct) => (
        <Product
          key={medicineProduct.id}
          subtitle="Essential for Women/Men"
          title={medicineProduct.name}
          desc={medicineProduct.description}
        />
      ))}
    </div>
  );
};

function Featured() {
  const [medicineProducts, setMedicineProducts] = React.useState([]);

  React.useEffect(() => {
    fetchMedicineProduct().then((medicineProducts) => {
      setMedicineProducts(medicineProducts);
    });
  }, []);

  return (
    <div className="featured-container">
      <div className="container-header">
        <h2>Featured Products</h2>
        <div className="content-link">
          <a className="link-underline" href="#home">
            View All
          </a>
        </div>
      </div>
      <div className="featured-content">
        <Products medicineProducts={medicineProducts} />
      </div>
    </div>
  );
}

export default Featured;
