import React, { useEffect, useState } from "react";
import "../styles/Featured.css";
import axios from 'axios';

const Products = (props) => {
  return (
    <div className="product">
      <div className="image-section">
        <div className="image">
          <div className="more">Learn More</div>
        </div>
      </div>
      <div className="product-description">
        <em>{props.subtitle}</em>
        <div className="product-link">
          <a href="#home">{props.title}</a>
        </div>
        <div className="product-info">{props.desc}</div>
      </div>
    </div>
  );
};

function Featured() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Make an API request to fetch products
    axios.get('http://localhost:3600/api/products/')
      .then((response) => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="featured-container">
      <div className="container-header">
        <h2>Featured Products</h2>
        <div className="content-link">
          <a className="link-underline" href="#home">
            View All
          </a>
        </div>
      </div>
      <div className="featured-content">
        {loading ? (
          <div>Loading...</div>
        ) : (
          <div className="products">
            {products.map((product) => (
              <Products
                key={product.id}
                subtitle="Essential for Women/Men"
                title={product.name}
                desc={product.description}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Featured;
