import "../styles/Featured.css";

const myUrl = document.location.search;
     console.log(myUrl);
     let params = new URLSearchParams(myUrl);
     let myId = params.get("id");
     console.log(myId);
  let url = 'http://localhost:3600/api/products/' + myId;
  fetch(url)
   .then((res) => res.json())
   .then((data) =>
   {
     const products = data;
     console.log(products);
     const item__img = document.getElementById('item__img');
     const title = document.getElementById('title');
     const  price = document.getElementById('price');
     const description = document.getElementById('description');
     const colors = document.getElementById('colors');

     item__img.innerHTML = 	`<img src="${products.imageUrl}" alt="${products.altTxt}">`;
         title.innerHTML = `${products.name}`;
         price.innerHTML = `${products.price}`;
         description.innerHTML = `${products.description}`;
         for (const color in products.colors){
         colors.innerHTML += ` <option value="${products.colors[color]}">${products.colors[color]}</option>`;
         }

    
    }); 
const Products = (props) => {
  return (
    <div className="product">
      <div className="image-section">
        <div className="image">
          <div className="more">Learn More</div>
        </div>
      </div>
      <div className="product-description">
        <em>{props.subtitle}</em>
        <div className="product-link">
          <a href="#home">{props.title}</a>
        </div>
        <div className="product-info">{props.desc}</div>
      </div>
    </div>
  );
};
function Featured() {
  return (
    <div className="featured-container">
      <div className="container-header">
        <h2>Featured Products</h2>
        <div className="content-link">
          <a className="link-underline" href="#home">
            View All
          </a>
        </div>
      </div>
      <div className="featured-content">
        <div className="products">
          <Products subtitle="Essential for Women/Men" title="Product Title" desc="[Brief description]" />
          <Products subtitle="Essential for Women/Men" title="Product Title" desc="[Brief description]" />
          <Products subtitle="Essential for Women/Men" title="Product Title" desc="[Brief description]" />
          <Products subtitle="Essential for Women/Men" title="Product Title" desc="[Brief description]" />
        </div>
      </div>
    </div>
  );
}

export default Featured;
