import { connect } from "react-redux";
import { useEffect } from "react";
import Product from "./product"; // Ensure the correct capitalization of the component name
import { fetchMedicineProductsRequest, fetchMedicineProductsSuccess, fetchMedicineProductsFailure } from "../redux/actions/productAction"; // Ensure the correct path to the actions

function Featured(props) {
  const { medicineProducts, isLoading, error, fetchMedicineProductsSuccess } = props;

  useEffect(() => {
    fetchMedicineProducts();
    console.log('Medicine Products:', fetchMedicineProducts);
  }, [fetchMedicineProducts]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  console.log("Medicine Products:", medicineProducts);

    return (
      <div className="featured-container">
        {medicineProducts ? (
          medicineProducts.map((medicineProduct) => (
            <Product
            
              key={medicineProduct.id}
              subtitle="Essential for Women/Men"
              title={medicineProduct.name}
              desc={medicineProduct.description}
            />
          ))
        ) : (
          <div>Loading...</div>
        )}
      </div>
    );
    
  
}

const mapStateToProps = (state) => ({
  medicineProducts: state.medicineProducts,
  isLoading: state.isLoading,
  error: state.error,
});

const mapDispatchToProps = {
  fetchMedicineProducts: fetchMedicineProductsRequest, // Use the action creator for the request
};

export default connect(mapStateToProps, mapDispatchToProps)(Featured);
