import React, { useEffect, useState } from "react";
import "../styles/Featured.css";

const Product = (props) => {
  return (
    <div className="product">
      <div className="image-section">
        <div className="image">
          <div className="more">Learn More</div>
        </div>
      </div>
      <div className="product-description">
        <em>{props.subtitle}</em>
        <div className="product-link">
          <a href="#home">{props.title}</a>
        </div>
        <div className="product-info">{props.desc}</div>
      </div>
    </div>
  );
};

function Featured() {
  const [medicineProducts, setMedicineProducts] = useState([]);

  useEffect(() => {
    async function fetchMedicineProducts() {
      try {
        const response = await fetch('http://localhost:3600/api/products');

        if (response.status === 200) {
          const medicineProductData = await response.json();
          setMedicineProducts(medicineProductData);
        } else {
          console.error('Failed to fetch medicine product data');
        }
      } catch (error) {
        console.error('Error while fetching data:', error);
      }
    }

    fetchMedicineProducts();
  }, []);

  return (
    <div className="featured-container">
      <div className="container-header">
        <h2>Featured Products</h2>
        <div className="content-link">
          <a className="link-underline" href="#home">
            View All
          </a>
        </div>
      </div>
      <div className="featured-content">
        <div className="products">
          {medicineProducts.map((medicineProduct) => (
            <Product
              key={medicineProduct.id} // Add a unique key based on the product's ID
              subtitle="Essential for Women/Men"
              title={medicineProduct.name}
              desc={medicineProduct.description}
            />
          ))}
          
        </div>
      </div>
    </div>
  );
}

export default Featured;
